import React from "react";
import styled from "styled-components";
import { faEnvelope } from "@fortawesome/free-solid-svg-icons";
import { faGithub } from "@fortawesome/free-brands-svg-icons";

import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";

const St = {
  Footer: styled.footer`
    display: block;
    width: 100%;
    height: 100%;
    /* height: 120px; */
    background-color: white;
    padding: 20px 0;
    @media ${({ theme }) => theme.tablet} {
      display: none;
    }
  `,
  Container: styled.div`
    display: flex;
    justify-content: space-between;
    align-items: center;
    width: 100%;
    height: 100%;
    max-width: 1360px;
    margin: 0 auto;
    padding: 0 20px;

    @media ${({ theme }) => theme.tablet} {
      display: block;
      max-width: 950px;
    }
  `,
  MainLink: styled.a`
    display: block;
    background-image: ${({ logo }) => `url(${logo})`};
    background-repeat: no-repeat;
    background-position: center;
    background-size: contain;
    color: transparent;
    width: 130px;
    height: 60px;
  `,
  Description: styled.p`
    font-weight: 600;
    font-size: 0.9rem;
    color: gray;
    height: 85px;
    margin-top: 10px;
    /* margin-left: 250px; */
  `,
  DescSpan: styled.span`
    display: block;
    height: 30px;
  `,
  ContactContainer: styled.address`
    display: flex;
    flex-direction: column;
    /* margin-left: 250px; */
  `,
  LinkTitle: styled.span`
    height: 25px;
    font-size: 0.9rem;
    font-weight: 600;
    color: gray;
  `,
  LinkTag: styled.a`
    display: flex;
    align-items: center;
    height: 30px;
    color: black;
    text-decoration: none;
  `,
  LinkSpan: styled.span`
    display: block;
    margin-left: ${({ marginLeft }) => marginLeft || "8px"};
    font-weight: 600;
    font-size: 0.9rem;
    height: 20px;
    /* line-height: 1.5rem; */
    color: gray;
  `,
};

const Footer = () => {
  return (
    <St.Footer>
      <St.Container>
        <St.MainLink
          href="/"
          title={"메인으로 이동"}
          logo={process.env.PUBLIC_URL + "/ocologo.png"}
        />
        <St.Description>
          <St.DescSpan>Coin Community Web Page Project </St.DescSpan>
          {/* <St.DescSpan>Created by Seongkyun Yu</St.DescSpan> */}
          <St.DescSpan>
            Cooooooooooooin
          </St.DescSpan>
          <St.DescSpan>
            Cooooooooooooin
          </St.DescSpan>
        </St.Description>
        <St.ContactContainer>
          <St.LinkTitle>Coooin</St.LinkTitle>
          <ul>
            <li>
              <St.LinkTag href="https://github.com/Seongkyun-Yu/upbit-clone">
                <FontAwesomeIcon
                  icon={faGithub}
                  size="lg"
                  title={"Github 아이콘"}
                />
                <St.LinkSpan>BitCoin</St.LinkSpan>
              </St.LinkTag>
            </li>
            <li>
              <St.LinkTag href="mailto:ysungkyun@gmail.com">
                <FontAwesomeIcon
                  icon={faEnvelope}
                  size="lg"
                  title={"이메일 아이콘"}
                />
                <St.LinkSpan>UFT</St.LinkSpan>
              </St.LinkTag>
            </li>
          </ul>
        </St.ContactContainer>
      </St.Container>
    </St.Footer>
  );
};

export default Footer;
